int
init_geu(int lgno) {

    register int  i;
    unsigned long cid;

    if (geu_MAX_TRIGs) {
        languages[lgno].dict = make_dict(2 * geu_MAX_TRIGs, 0);
        languages[lgno].hash = (double *)malloc(sizeof(double) * geu_MAX_TRIGs);
        if (!languages[lgno].hash) return(1);
        for (i = 0; i < geu_MAX_TRIGs; i++) {
           cid = string2id((char *)geu_hash[i].trig, languages[lgno].dict, insert_copy);
           if (cid == ~0) return(1);
           languages[lgno].hash[cid] = geu_hash[i].prob;
        }
        languages[lgno].wdict = make_dict(2 * 80, 0);
        languages[lgno].whash = (double *)malloc(sizeof(double) * 80);
        for ( i = 0; i < 79; i++) {
           cid = string2id((char *)geu_wordhash[i].word, languages[lgno].wdict, insert_copy);
           if (cid == ~0) return(1);
           languages[lgno].whash[cid] = geu_wordhash[i].prob;
        }
    } else languages[lgno].used = 0;

    return(0);
}
