int
init_heu(int lgno) {

    register int  i;
    unsigned long cid;

    if (heu_MAX_TRIGs) {
        languages[lgno].dict = make_dict(2 * heu_MAX_TRIGs, 0);
        languages[lgno].hash = (double *)malloc(sizeof(double) * heu_MAX_TRIGs);
        if (!languages[lgno].hash) return(1);
        for (i = 0; i < heu_MAX_TRIGs; i++) {
           cid = string2id((char *)heu_hash[i].trig, languages[lgno].dict, insert_copy);
           if (cid == ~0) return(1);
           languages[lgno].hash[cid] = heu_hash[i].prob;
        }
        languages[lgno].wdict = make_dict(2 * 100, 0);
        languages[lgno].whash = (double *)malloc(sizeof(double) * 100);
        for ( i = 0; i < 99; i++) {
           cid = string2id((char *)heu_wordhash[i].word, languages[lgno].wdict, insert_copy);
           if (cid == ~0) return(1);
           languages[lgno].whash[cid] = heu_wordhash[i].prob;
        }
    } else languages[lgno].used = 0;

    return(0);
}
